import React, {Component} from 'react';
import {Button, Modal} from 'semantic-ui-react';

class AddMedicine extends Component{

    state = { open: false }

    show = size => () => this.setState({ size, open: true })
    close = () => this.setState({ open: false })

    render(){

        const { open, size } = this.state

        return(
            <span>
               <Button onClick={this.show('mini')} color="blue">Add Medicine</Button>

                <Modal size={size} open={open} onClose={this.close}>
                    <Modal.Header>Add Medicine</Modal.Header>
                    <Modal.Content>

                    </Modal.Content>
                    <Modal.Actions>
                        <Button onClick={this.close} negative>No</Button>
                        <Button onClick={this.close} positive icon='checkmark' labelPosition='right' content='Add' />
                    </Modal.Actions>
                </Modal>
            </span>
        );
    }
}

export default AddMedicine;
